mapper.update:
UPDATE STORE SET ST_STNAME =#{storeName},
ST_INFO=#{storeInfo}
,

	ST_INFODETAIL = #{storeInfoDetail}

,
ST_PHONE=#{storePhone}
,ST_CATEGORY=#{storeCategory}
WHERE ST_CODE = #{storeCode}

UPDATE STOREMENU
SET SM_NAME = #{menuList[0].menuName}, SM_PRICE = #{menuList[0].menuPrice}, SM_SICODE = #{menuList[0].menuImageCode}, SM_ISMAIN = #{menuList[0].isMain},SM_ISKIOSK = #{menuList[0].isKiosk}, SM_ISSOLD = #{menuList[0].isSold}
WHERE SM_STCODE = #{storeCode} AND SM_CODE = #{menuList[0].menuCode}

mapper.delete:
DELETE FROM STOREMENU WHERE
SM_STCODE = #{storeCode} AND
SM_NAME = #{menuList[0].menuName}

mapper.insert:
INSERT INTO POSDBA.LOCATION (LO_STCODE,LO_CODE,LO_NAME,LO_LATITUDE,LO_LONGITUDE,LO_DETAIL,LO_ADDR)
VALUES(#{storeCode},(SELECT'L'||LPAD(NVL(SUBSTR(MAX(LO_CODE),2,2),-1)+1,2,'0') FROM LOCATION WHERE LO_STCODE = #{storeCode}),#{locationList[0].locationName}
,#{locationList[0].latitude},#{locationList[0].longitude},#{locationList[0].locationDetail},#{locationList[0].locationAddr})
