mapper.update:
update store set st_isopen=#{salesLogList[0].salesState}
, st_locode =
#{locationList[0].locationCode} where st_code
=#{storeCode}

UPDATE STORE SET ST_STNAME
=#{storeName},st_locode=#{storeLocationCode},
ST_INFO=#{storeInfo}
,

	ST_INFODETAIL = #{storeInfoDetail}

,
ST_PHONE=#{storePhone}
,ST_CATEGORY=#{storeCategory}
WHERE ST_CODE = #{storeCode}

UPDATE STOREMENU
SET SM_NAME = #{menuList[0].menuName}, SM_PRICE = #{menuList[0].menuPrice}, SM_SICODE = #{menuList[0].menuImageCode}, SM_ISMAIN = #{menuList[0].isMain}, SM_ISKIOSK = #{menuList[0].isKiosk}, SM_ISSOLD = #{menuList[0].isSoldOut}
WHERE SM_STCODE = #{storeCode} AND SM_CODE = #{menuList[0].menuCode}

mapper.delete:
DELETE FROM STOREMENU WHERE
SM_STCODE = #{storeCode} AND
SM_CODE = #{menuList[0].menuCode}
